sql.if:
and exists(
    select 1 from nl_tablestructure_info e1
    where e1.id = t.table_id

    and e1.`table_name` = #{tableName}

)

mapper.select:
select
,c1.`table_name` as tableName from nl_tablefield_info t left outer join nl_tablestructure_info c1 on c1.id = t.table_id
limit #{startIndex},#{pageSize}
