sql.if:
and exists(
    select 1 from nl_tablespace_info e1
    where e1.id = t.tablespace_id

    and e1.tablespace_name = #{tablespaceName}

)

mapper.select:
select
,c1.tablespace_name as tablespaceName from nl_tablestructure_info t left outer join nl_tablespace_info c1 on c1.id = t.tablespace_id
limit #{startIndex},#{pageSize}

select
    t.id as `key`,
    t.`table_name` as `value`
from nl_tablestructure_info t


        and t.id > #{lastKey}


        and t.`table_name` = #{matchValue}


order by t.id asc
limit #{limit}
